import React from 'react'
import * as axios from 'axios'

export class WeatherForm extends React.Component {
    constructor() {
        super();

        this.state = {
            city: '',
            info: {}
        }
    }

    onChangeHandler = (e) => {
        this.setState({city: e.target.value})
    }

    onSubmitHandler = (e) => {
        e.preventDefault()
        axios.get(`http://api.openweathermap.org/data/2.5/weather?q=${this.state.city}&appid=04fbd09dfff38bda103004c4674e1773`)
            .then( res => {
                this.setState({info: res.data})
            })

    }

    render() {
        return (
            <div>
                <form onSubmit={this.onSubmitHandler}>
                    <input type="text" value={this.state.city} onChange={this.onChangeHandler}/>
                    <button type='submit'>search</button>
                </form>
            </div>
        )
    }
}
